class SpacedRepetition {
  constructor() {
    this.intervals = [1, 3, 7, 14, 30, 90]; // days
  }

  calculateNextReview(currentInterval, performance) {
    // Performance: 1-5 scale
    if (performance >= 4) {
      // Good performance - increase interval
      const nextIndex = this.intervals.indexOf(currentInterval) + 1;
      return this.intervals[Math.min(nextIndex, this.intervals.length - 1)];
    } else {
      // Poor performance - reset to beginning
      return this.intervals[0];
    }
  }

  scheduleCard(card, performance) {
    const nextInterval = this.calculateNextReview(card.interval, performance);
    const nextReview = new Date();
    nextReview.setDate(nextReview.getDate() + nextInterval);

    return {
      ...card,
      interval: nextInterval,
      nextReview: nextReview,
      reviewCount: card.reviewCount + 1
    };
  }
}

class LearningTracker {
  constructor() {
    this.sessions = [];
    this.goals = [];
  }

  addSession(topic, duration, understanding, notes) {
    const session = {
      id: Date.now(),
      topic,
      duration,
      understanding, // 1-10 scale
      notes,
      date: new Date()
    };
    this.sessions.push(session);
    return session;
  }

  setGoal(description, targetDate, category) {
    const goal = {
      id: Date.now(),
      description,
      targetDate: new Date(targetDate),
      category,
      achieved: false,
      createdAt: new Date()
    };
    this.goals.push(goal);
    return goal;
  }

  getProgressSummary() {
    const totalSessions = this.sessions.length;
    const avgUnderstanding = this.sessions.reduce(
      (sum, session) => sum + session.understanding, 0
    ) / totalSessions;

    const completedGoals = this.goals.filter(goal => goal.achieved).length;
    const goalCompletionRate = (completedGoals / this.goals.length) * 100;

    return {
      totalSessions,
      averageUnderstanding: avgUnderstanding.toFixed(1),
      goalCompletionRate: goalCompletionRate.toFixed(1),
      recentTrend: this.calculateTrend()
    };
  }

  calculateTrend() {
    if (this.sessions.length < 5) return 'Insufficient data';

    const recent = this.sessions.slice(-5);
    const older = this.sessions.slice(-10, -5);

    const recentAvg = recent.reduce((sum, s) => sum + s.understanding, 0) / recent.length;
    const olderAvg = older.reduce((sum, s) => sum + s.understanding, 0) / older.length;

    if (recentAvg > olderAvg + 0.5) return 'Improving';
    if (recentAvg < olderAvg - 0.5) return 'Declining';
    return 'Stable';
  }
}
